create table chat_message
(
    id           bigint auto_increment
        primary key,
    content      varchar(255)                   null,
    recipient_id bigint                         null,
    sender_id    bigint                         null,
    timestamp    datetime(6)                    null,
    type         enum ('CHAT', 'JOIN', 'LEAVE') null
);

create table jobs
(
    id          bigint auto_increment
        primary key,
    about       varchar(255)                                              null,
    company     varchar(255)                                              null,
    description varchar(255)                                              null,
    experience  varchar(255)                                              null,
    job_status  enum ('ACTIVE', 'CLOSED', 'DRAFT', 'PENDING', 'REJECTED') null,
    job_title   varchar(255)                                              null,
    job_type    varchar(255)                                              null,
    location    varchar(255)                                              null,
    post_time   datetime(6)                                               null,
    posted_by   bigint                                                    null,
    salary      bigint                                                    null
);

create table job_skills_required
(
    job_id          bigint       not null,
    skills_required varchar(255) null,
    constraint FKmhuo0eithtxl11s73oky3ek3n
        foreign key (job_id) references jobs (id)
);

create table profiles
(
    id           bigint auto_increment
        primary key,
    about        varchar(255) null,
    account_type varchar(255) null,
    company      varchar(255) null,
    email        varchar(255) null,
    job_title    varchar(255) null,
    location     varchar(255) null,
    name         varchar(255) null,
    picture      longblob     null,
    total_exp    bigint       null
);

create table profile_certifications
(
    profile_id     bigint       not null,
    certificate_id varchar(255) null,
    issue_date     datetime(6)  null,
    issuer         varchar(255) null,
    name           varchar(255) null,
    constraint FK6ts9728b18v4yp2fi5rcmef2
        foreign key (profile_id) references profiles (id)
);

create table profile_experiences
(
    profile_id  bigint       not null,
    company     varchar(255) null,
    description varchar(255) null,
    end_date    datetime(6)  null,
    location    varchar(255) null,
    start_date  datetime(6)  null,
    title       varchar(255) null,
    working     bit          null,
    constraint FK1ube9d7o341x0u1hrxecb3axy
        foreign key (profile_id) references profiles (id)
);

create table profile_saved_jobs
(
    profile_id bigint not null,
    saved_jobs bigint null,
    constraint FKl973d364mo4sv3djlj3fl4xby
        foreign key (profile_id) references profiles (id)
);

create table profile_skills
(
    profile_id bigint       not null,
    skills     varchar(255) null,
    constraint FK4l5ld8jvogl0skpynyxle9var
        foreign key (profile_id) references profiles (id)
);

create table users
(
    id              bigint auto_increment
        primary key,
    account_status  tinyint                                 null,
    account_type    enum ('ADMIN', 'APPLICANT', 'EMPLOYER') null,
    email           varchar(255)                            null,
    last_login_date datetime(6)                             null,
    name            varchar(255)                            null,
    password        varchar(255)                            null,
    profile_id      bigint                                  null,
    constraint UK6dotkott2kjsp8vw4d0m25fb7
        unique (email),
    constraint UK7s5nlreekaxdbfml4ofky7utw
        unique (profile_id),
    constraint FKq2e6rj0p6p1gec2cslmaxugw1
        foreign key (profile_id) references profiles (id),
    check (`account_status` between 0 and 4)
);

create table applicant
(
    id                 bigint auto_increment
        primary key,
    application_status tinyint      null,
    cover_letter       varchar(255) null,
    extracted_resume   longblob     null,
    interview_time     datetime(6)  null,
    phone              varchar(255) null,
    resume             longblob     null,
    timestamp          datetime(6)  null,
    website            varchar(255) null,
    job_id             bigint       null,
    applicant_id       bigint       not null,
    constraint FKqa1uqmbu7dp6gnt89hqkfsbfy
        foreign key (applicant_id) references users (id),
    constraint FKr8dwtf3n11aq6t44ve59qcigk
        foreign key (job_id) references jobs (id),
    check (`application_status` between 0 and 3)
);

create table cv
(
    id            bigint auto_increment
        primary key,
    education     varchar(255) null,
    experience    varchar(255) null,
    personal_info varchar(255) null,
    skills        varchar(255) null,
    user_id       bigint       not null,
    constraint FKq1uw3pyveaumh2hq2md9istwe
        foreign key (user_id) references users (id)
);

create table notifications
(
    id        bigint auto_increment
        primary key,
    action    varchar(255) null,
    message   varchar(255) null,
    route     varchar(255) null,
    status    tinyint      null,
    timestamp datetime(6)  null,
    user_id   bigint       not null,
    constraint FK9y21adhxn0ayjhfocscqox7bh
        foreign key (user_id) references users (id),
    check (`status` between 0 and 1)
);

create table otp
(
    id            bigint auto_increment
        primary key,
    creation_time datetime(6)  null,
    otp_code      varchar(255) null,
    user_id       bigint       not null,
    constraint FKs0hlsjury48cekfbfusk11lyr
        foreign key (user_id) references users (id)
);

